import React, { useState } from "react";

export default function Form() {
  const [toDo, setTodo] = useState("");
  const [toDos, setTodos] = useState([
    {
      id: 1,
      text: "청소하기",
    },
  ]);
  const onChange = (e) => {
    setTodo(e.target.value);
  };
  const onSubmit = (e) => {
    e.preventDefault();
    toDo !== "" && setTodos((current) => [...current, toDo]);
    setTodo("");
  };
  return (
    <div>
      <ul>
        <li>{toDos.map((prev) => prev.text)}</li>
      </ul>
      <form onSubmit={onSubmit}>
        <input type="text" placeholder="write & enter" value={toDo.text} onChange={onChange} />
        <button>제출</button>
      </form>
    </div>
  );
}
